// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Products from './pages/Products';
import Cart from './pages/Cart';
import OrderHistory from './pages/OrderHistory';
import Checkout from './pages/Checkout';
import ProtectedRoute from './components/ProtectedRoute';
import BlogSection from './components/BlogSection';
import BlogPostPage from './pages/BlogPostPage'; // Import the BlogPostPage component
import './App.css';

function App() {
  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/products" element={<Products />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route
          path="/order-history"
          element={<ProtectedRoute element={<OrderHistory />} />}
        />
        <Route path="/blog" element={<BlogSection />} /> {/* Add this route */}
        <Route path="/blog/:id" element={<BlogPostPage />} /> {/* Add this route */}
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
